"use strict";

var test = require('tape');
var chunk = require('./');

test('exists', function(t) {
    t.equal(typeof chunk, 'function');
    t.end();
});

test('return an array', function(t) {
    t.equal(Array.isArray(chunk()), true);
    t.equal(Array.isArray(chunk([])), true);
    t.equal(Array.isArray(chunk(null)), true);
    t.equal(Array.isArray(chunk(123)), true);
    t.end();
});

test('return an same array if no size', function(t) {
    t.deepEqual(chunk([]), []);
    t.deepEqual(chunk([1]), [1]);
    t.deepEqual(chunk([1, 2, 3, 4]), [1, 2, 3, 4]);
    t.end();
});

test('split up array based on size', function(t) {
    t.deepEqual(chunk([], 2), []);
    t.deepEqual(chunk([1], 2), [1]);
    t.deepEqual(chunk([1, 2, 3, 4], 2), [[1, 2], [3, 4]]);
    t.deepEqual(chunk([1, 2, 3, 4], 3), [[1, 2, 3], [4]]);
    t.deepEqual(chunk([1, 2, 3, 4], 1), [[1], [2], [3], [4]]);
    t.deepEqual(chunk(['a', 'b', 'c', 'd'], 2), [['a', 'b'], ['c', 'd']]);
    t.deepEqual(chunk(['a', 'b', 'c', 'd'], 3), [['a', 'b', 'c'], ['d']]);
    t.end();
});
