"use strict";

var test = require('tape');
var every = require('./');

test('exists', function(t) {
    t.equal(typeof every, 'function');
    t.end();
});

test('return a boolean', function(t) {
    t.equal(every(), false);
    t.equal(every(null, Boolean), false);
    t.equal(every([], null), false);
    t.end();
});

test('arrays', function(t) {
    t.test('compare all objects', function(t) {
        function isBoolean(v) {
            return typeof v === 'boolean';
        }
        t.equal(every([true, 1, null, 'yes'], isBoolean), false);
        t.equal(every([true, false, true], isBoolean), true);
        t.end();
    });
    t.end();
});
