"use strict";

var test = require('tape');
var isArray = require('./');

test('exists', function(t) {
    t.equal(typeof isArray, 'function');
    t.end();
});

test('return false', function(t) {
    t.false(isArray());
    t.false(isArray(null));
    t.false(isArray(123));
    t.false(isArray("abc"));
    t.end();
});

test('return true', function(t) {
    t.true(isArray([]));
    t.end();
});
