"use strict";

var test = require('tape');
var isFunction = require('./');

test('exists', function(t) {
    t.equal(typeof isFunction, 'function');
    t.end();
});

test('return false', function(t) {
    t.false(isFunction());
    t.end();
});

test('return true', function(t) {
    t.true(isFunction(function() {}));
    t.end();
});
