"use strict";

var test = require('tape');
var isNull = require('./');

test('exists', function(t) {
    t.equal(typeof isNull, 'function');
    t.end();
});

test('return a boolean', function(t) {
    t.equal(isNull(null), true);
    t.equal(isNull(), false);
    t.equal(isNull(undefined), false);
    t.equal(isNull(void 0), false);
    t.equal(isNull(123), false);
    t.equal(isNull("abc"), false);
    t.end();
});
