"use strict";

var test = require('tape');
var isUndefined = require('./');

test('exists', function(t) {
    t.equal(typeof isUndefined, 'function');
    t.end();
});

test('return a boolean', function(t) {
    t.equal(isUndefined(), true);
    t.equal(isUndefined(undefined), true);
    t.equal(isUndefined(void 0), true);
    t.equal(isUndefined(null), false);
    t.equal(isUndefined(123), false);
    t.equal(isUndefined("abc"), false);
    t.end();
});
