"use strict";

var test = require('tape');
var keys = require('./');

test('exists', function(t) {
    t.equal(typeof keys, 'function');
    t.end();
});

test('return an array', function(t) {
    t.true(Array.isArray(keys()));
    t.true(Array.isArray(keys(null)));
    t.true(Array.isArray(keys(null, 123)));
    t.true(Array.isArray(keys(123, null)));
    t.end();
});

test('return keys of the object', function(t) {
    function Foo() {
        this.a = 1;
        this.b = 2;
    }
    t.deepEqual(keys(new Foo()), ['a', 'b']);
    t.deepEqual(keys('hi'), ['0', '1']);
    t.deepEqual(keys({
        'user': 'fred',
        'age': 40
    }), ['user', 'age']);
    t.end();
});
