"use strict";

var test = require('tape');
var some = require('./');

test('exists', function(t) {
    t.equal(typeof some, 'function');
    t.end();
});

test('return a boolean', function(t) {
    t.equal(some(), false);
    t.equal(some(null, Boolean), false);
    t.equal(some([], null), false);
    t.end();
});

test('arrays', function(t) {
    t.test('compare all objects', function(t) {
        function isBoolean(v) {
            return typeof v === 'boolean';
        }
        t.equal(some([undefined, 1, null, 'yes'], isBoolean), false);
        t.equal(some([null, 0, 'yes', false], isBoolean), true);
        t.end();
    });
    t.end();
});
