"use strict";

var test = require('tape');
var uniqueId = require('./');

test('exists', function(t) {
    t.equal(typeof uniqueId, 'function');
    t.end();
});

test('return a higher number each call', function(t) {
    var first = uniqueId();
    t.equal(typeof first, 'number');
    t.equal(first, 1);
    t.equal(uniqueId(), 2);
    t.equal(uniqueId(), 3);
    t.equal(uniqueId(), 4);
    t.end();
});
